        }
    end,
    collection_loc_vars = function (self)
        return {
            vars = { 0.75 }
        }
    end,
    calculate = function (self, blind, context)
        if context.individual and (context.cardarea == G.hand or context.cardarea == G.play or context.cardarea == "unscored") then
