        info_queue[#info_queue+1] = {set = "DescriptionDummy", key = "dd_akyrs_placeholder_art"}
        local _,_2,c = love.window.getMode()
        local x = math.max(c.refreshrate - love.timer.getFPS( ),0) 
        if AKYRS.bal("absurd") then
            return {
