    key = "umbral_mantle",
    atlas = "umbra",
    pos = {x=9,y=1},
    config = {
        extras = 0.1
    },
    can_use = function (self, card)
        return #G.hand.cards > 0
    end,
    loc_vars = function (self, info_queue, card)
        return {
            vars = {
                card.ability.extras
            }
        }
    end,
    use = function (self, card, area, copier)
        AKYRS.juice_like_tarot(card)
        for _,_c in ipairs(G.hand.cards) do
