                AKYRS.simple_event_add(
                    function ()
                        _c.ability.perma_x_chips = _c.ability.perma_x_chips + card.ability.extras
                        _c:juice_up(0.3, 0.3)
                        play_sound("tarot1")
                        return true
                    end
                )
            end
        end
    end

