key = 'aries',
    set = 'Horoscope',
    atlas = 'Consumables',
    pos = {
        x = 0,
        y = 1
    },
    mxms_credits = {
        art = { "Maxiss02" },
        code = { "theAstra" },
        idea = { "Maxiss02" }
    },
    cost = 4,
    loc_vars = function(self, info_queue, card)
        info_queue[#info_queue + 1] = G.P_TAGS['tag_mxms_ram']
    end,
    calculate = function(self, card, context)
        if (context.joker_main or context.debuffed_hand) and G.GAME.blind.triggered then
            self:succeed(card)
